import React from 'react';

const countryList = [
    "GBP", "CAD", "USD", "AUD", "BGN", "BRL", "CHF", "CNY", "CZK", "DKK", "EUR", "HKD", "HRK", "HUF", "IDR", "ILS", "INR", "ISK", "JPY", "KRW", "MXN", "MYR", "NOK", "NZD", "PHP", "PLN", "RON", "RUB", "SEK", "SGD", "THB", "TRY", "ZAR"
]

class CurrencyConverter extends React.Component {
    constructor (props) {
        super(props);
        this.state = {
            value1: 1,
            value2: 1,
            rates: [],
            choice1: 'GBP',
            choice2: 'GBP',
            math1: 1,
            math2: 1,
        }
        this.handleNumChange1 = this.handleNumChange1.bind(this);
        this.handleNumChange2 = this.handleNumChange2.bind(this);
        this.handleChange1 = this.handleChange1.bind(this);
        this.handleChange2 = this.handleChange2.bind(this);
        this.fetchRequest = this.fetchRequest.bind(this);

    }


    fetchRequest = (choice1,choice2) => {
        fetch('https://api.exchangeratesapi.io/latest?base='+ (choice1)).then((response) => {
            console.log(choice1);
            console.log(choice2);

         if (response.ok) {
          return response.json();
        }
        throw new Error('Request was either a 404 or 500');
         }).then((data) => {
                this.setState({ 
                    rates: data.rates,
                })
            console.log('json data', data);
            this.setState({math1: this.state.rates[choice1]});
            this.setState({math2: this.state.rates[choice2]});
            }).catch((error) => {
            console.log(error);
        })
    }

    handleChange1 = (e) => {
        this.setState({choice1: e.target.value},this.fetchRequest(e.target.value));     
    }

    handleChange2 = (e) => {
        this.setState({choice2: e.target.value},this.fetchRequest(e.target.value));     
    }

    handleNumChange1 = (e) => {
        this.setState({value1: e.target.value}, this.handleNumChange1());
        const calc1 = (this.state.value1) * (this.state.math2);
        this.setState({value2: (calc1)});
    }

    handleNumChange2 = (e) => {
        this.setState({value2: e.target.value});
        const calc2 = (this.state.value2) * (this.state.math1);
        this.setState({value1: (calc2)}); 

    }

    // handleChange: function(e) {
    //     console.log(e.target.value);
    //     this.setState({message: e.target.value}, this.handleSubmit);
    // }

    render() {
        return (
          <div id="converterdiv">
              <h1 id="curexchange">Currency Exchanger</h1>
              <hr></hr>
             <select onChange={this.handleChange1}>
               {countryList.map((country) => <option key={country} label={country} value={country}></option>)}
             </select>

             <input onChange={this.handleNumChange1} className="d-inline cconverter" type="number" placeholder="Value" label="Value" value={this.state.value1}></input>
             <p className="d-inline cconverter">Exchanges To</p>
             <select onChange = {this.handleChange2}>
               {countryList.map((country) => <option key={country} label={country} value={country}></option>)}
             </select>
            <input onChange={this.handleNumChange2} className="d-inline" type="number" placeholder="Value" label="Value" value={this.state.value2}></input>

          </div>
        );  
    }
}

export default CurrencyConverter;
