import React, { Component } from 'react';


const countryList = [
    "GBP", "CAD", "USD", "AUD", "BGN", "BRL", "CHF", "CNY", "CZK", "DKK", "EUR", "HKD", "HRK", "HUF", "IDR", "ILS", "INR", "ISK", "JPY", "KRW", "MXN", "MYR", "NOK", "NZD", "PHP", "PLN", "RON", "RUB", "SEK", "SGD", "THB", "TRY", "ZAR"
]


class CurrencyTable extends React.Component {
    constructor (props) {
        super(props);
        this.state = {
            value: "",
            rates: [],
            date: 'today',
            base: "USD",
        };
    this.handleBaseChange = this.handleBaseChange.bind(this);
    this.tableCreator = this.tableCreator.bind(this);
    }



    fetchRequest = (base) => {
        fetch('https://api.exchangeratesapi.io/latest?base='+(base)).then((response) => {
            console.log(base);

         if (response.ok) {
          return response.json();
        }
        throw new Error('Request was either a 404 or 500');
         }).then((data) => {
                this.setState({ 
                    date: data.date,
                    rates: data.rates,
                    base: data.base,
                })
            console.log('json data', data);
            this.tableCreator();
            }).catch((error) => {
            console.log(error);
        })
    }

    handleBaseChange = (event) => {
        this.setState({value: event.target.value});   
        const base = event.target.value;
        this.fetchRequest(base);
    }

    selectCreator = () => {
        const selectCountry = countryList.map((country) =>  <option className="option" label={country} value={country}></option>)
        return selectCountry;
    }

    tableCreator = () => {
