    }

    componentDidMount () {
        this.fetchRequest(this.state.base);
        this.selectCreator;
    }



    render() {
        return (
          <div id="tablediv">
              <div className="table-dark darkDiv">
              <h1 id="ctable" className="tableheading table-dark">Currency Table</h1>
              <label className="d-inline">
                  Base Currency : 
              <select className="dropDown" value={this.state.value} onChange={this.handleBaseChange}>
                <option className="option" label="USD" value="USD"></option>
                <selectCountry/>
                <option className="option" label="CAD" value="CAD"></option>
                <option className="option" label="GBP" value="GBP"></option>
              </select> 
              </label>
              <p className="table-dark">Rates are current as of {this.state.date}</p>     
              <table id="curtable" className="table table-dark">
                <tbody>
                  <tr>
                    <th>Currency:</th>
                    <th>Value:</th>
                  </tr>
                  <tr>
                    <td>CAD : </td>
                    <td>{this.state.rates.CAD}</td>
                  </tr>
                  <tr>
                    <td>GBP : </td>
                    <td>{this.state.rates.GBP}</td>
                  </tr>
                </tbody>
              </table>
              </div>
          </div>
        );  
    }
}

export default CurrencyTable;
