import React, { Component } from 'react';


class CurrencyTable extends React.Component {
    constructor (props) {
        super(props);
        this.state = {
            value: "USD",
            rates: [],
            date: '',
            base: "",
        };
    this.handleBaseChange = this.handleBaseChange.bind(this);
    this.tableCreator = this.tableCreator.bind(this);
    }



    fetchRequest = (base) => {
        fetch('https://api.exchangeratesapi.io/latest?base='+(base)).then((response) => {
            console.log(base);

         if (response.ok) {
          return response.json();
        }
        throw new Error('Request was either a 404 or 500');
         }).then((data) => {
                this.setState({ 
                    date: data.date,
                    rates: data.rates,
                    base: data.base,
                })
            console.log('json data', data);
            }).catch((error) => {
            console.log(error);
        })
    }

    handleBaseChange = (event) => {
        this.setState({value: event.target.value});   
        const base = event.target.value;
        this.fetchRequest(base);
        this.tableCreator();

    }

    tableCreator = () => {
        const tableRates = state.rates.map(rate => <td>{rate}</td>)
        return tableRates;
    }


    render() {
        return (
          <div id="tablediv">
              <div className="table-dark darkDiv">
              <h1 id="ctable" className="tableheading table-dark">Currency Table</h1>
              <label className="d-inline">
                  Base Currency:
              <select className="dropDown" value={this.state.value} onChange={this.handleBaseChange}>
                <option className="option" value="USD"></option>
                <option className="option" value="CAD"></option>
                <option className="option" value="GBP"></option>
              </select> 
              </label>
              <p className="table-dark">Rates are current as of {this.state.date}</p>     
              <table id="curtable" className="table table-dark">
                <tbody>
                  <tr>
                    <th>Currency: Value</th>
                  </tr>
                  <tr>

                  </tr>
                </tbody>
              </table>
              </div>
          </div>
        );  
    }
}

export default CurrencyTable;
