              <table id="curtable" className="table table-dark">
                  <tbody>
                    <tr>
                        <th>Currency: Value</th>
                    </tr>
                    <tr>
                        {/* {tableRates} */}
                    </tr>
                  </tbody>
              </table>
          </div>
        );  
    }
}

export default CurrencyTable;
