import React, { Component } from 'react';

 const fetchRequest = function(base) {
     const address = ('https://api.exchangeratesapi.io/latest?base='+{base});
    fetch(address).then((response) => {
     if (response.ok) {
      return response.json();
    }
    throw new Error('Request was either a 404 or 500');
     }).then((data) => {
            this.setState({ results: data.rates})
        console.log('json data', data);
        }).catch((error) => {
        console.log(error);
    })
}

const handleBase = (base) => {
    // take option from base selection and then input it into fetchrequest
    fetchRequest(base);
}


