import React, { Component } from 'react';

 const fetchRequest = function(base) {
    fetch('https://api.exchangeratesapi.io/latest?base='+(base)).then((response) => {
     if (response.ok) {
      return response.json();
    }
    throw new Error('Request was either a 404 or 500');
     }).then((data) => {
            this.setState({ results: data.rates})
        console.log('json data', data);
        }).catch((error) => {
        console.log(error);
    })
}

handleSubmit = (event) => {
    this.setState({value: event.target.value});   
}

const verify = () => {
    if (Array.currList.includes(base)) {
        fetchRequest(base);
    }else {
        const errorMessage = "Please select a valid currency"

    }
}
