import React, { Component } from 'react';

const countryList = [
    "GBP", "CAD", "USD", "AUD", "BGN", "BRL", "CHF", "CNY", "CZK", "DKK", "EUR", "HKD", "HRK", "HUF", "IDR", "ILS", "INR", "ISK", "JPY", "KRW", "MXN", "MYR", "NOK", "NZD", "PHP", "PLN", "RON", "RUB", "SEK", "SGD", "THB", "TRY", "ZAR"
]

class CurrencyConverter extends React.Component {
    constructor (props) {
        super(props);
        this.state = {
            choice1: '',
            choice2: "",
            rates: [],
            base: 'USD'
        }
        this.handleChange1 = this.handleChange1.bind(this);
        this.handleChange2 = this.handleChange2.bind(this);

    }

    handleChange1 = (event) => {
        console.log("base changed");
        this.setState({value: event.target.choice1});   
        const choice1 = event.target.choice1;
        console.log(choice1);
    }

    handleChange2 = (event) => {
        console.log("base changed");
        this.setState({value: event.target.choice2});   
        const choice2 = event.target.choice2;
        console.log(choice2);

    }

    handleClick = (event) => {
        console.log('clicked');
        // fetchRequest(choice1, choice2);
    }

    render() {
        return (
          <div id="converterdiv">
              <h1 id="curexchange">Currency Exchanger</h1>
              <hr></hr>
             <select onChange={this.handleChange}>
               {countryList.map((country) => <option key={country} label={country} choice1={country}></option>)}
             </select>

             <input className="d-inline cconverter" type="text" placeholder="Value" label="Value"></input>
             <p className="d-inline cconverter">Exchanges To</p>
             <select onChange = {this.handleChange}>
               {countryList.map((country) => <option key={country} label={country} choice2={country}></option>)}
             </select>
            <input className="d-inline" type="text" placeholder="Value" label="Value"></input>
            <button className="btn btn-danger d-block" id="exchange">Exchange!</button>

          </div>
        );  
    }
}

export default CurrencyConverter;
