import React, { Component } from 'react';



fetch('https://api.exchangeratesapi.io/latest?base=USD').then((response) => {
  if (response.ok) {
    return response.json();
  }
  throw new Error('Request was either a 404 or 500');
    }).then((data) => {
        this.setState({ results: data.rates})
    console.log('json data', data);
    }).catch((error) => {
    console.log(error);
})


// const tableRates = rates.map(rate => <td>{id}</td><td{}</td>);
