            // return <td>tableRates</td>);
// }

// const currencyDate= {
//     props.data.date;
//   takes date from fetch request and adds it to jsx
// return 
// } 


class CurrencyTable extends React.Component {
    constructor (props) {
        super(props);
        this.state = {
            rates: [],
            value: "USD",
            results: []
        };
    this.handleChange = this.handleChange.bind(this);
    }



fetchRequest = (base) => {
        fetch('https://api.exchangeratesapi.io/latest?base='+(base)).then((response) => {
            console.log(base)

         if (response.ok) {
          return response.json();
        }
        throw new Error('Request was either a 404 or 500');
         }).then((data) => {
                this.setState({ results: data.rates})
            console.log('json data', data);
            }).catch((error) => {
            console.log(error);
        })
    }

    handleChange = (event) => {
        this.setState({value: event.target.value});   
        const base = event.target.value;
        this.fetchRequest(base);
    }






    render() {
        return (
          <div id="tablediv">
              <div className="table-dark darkDiv">
              <h1 id="ctable" className="tableheading table-dark">Currency Table</h1>

              <select className="dropDown" value={this.state.value} onChange={this.handleChange}>
                <option className="option" value="USD"></option>
                <option className="option" value="CAD"></option>
                <option className="option" value="GBP"></option>
              </select> 
              <p className="table-dark">Base Currency</p>  
              <p className="table-dark">Rates are current as of date</p>     
              <table id="curtable" className="table table-dark">
                <tbody>
                  <tr>
                    <th>Currency: Value</th>
                  </tr>
                  <tr>
                        {/* {tableRates} */}
                  </tr>
                </tbody>
              </table>
              </div>
          </div>
        );  
    }
}

export default CurrencyTable;
